require 'rails_helper'

RSpec.feature "User can edit an artist" do
  scenario "user can edit an artist" do
    artist = Artist.create(name: "Biggie Small", image_path: "data:image/jpeg;base64,")
    updated_artist_name = "Notorious"

    visit artist_path(artist)
    # _page
    click_on "Edit"

    fill_in "artist_name", with: updated_artist_name
    # _page
    click_on "Update Artist"
    # _page
    expect(page).to have_content updated_artist_name
    expect(page).to have_css("img[src=\"#{artist.image_path}\"]")
  end
end
